package demo;

public class Demo11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		while(i <= 10) {
			System.out.println(" " + i);
			i++;
		}
		System.out.println();
		int j = 1;
		do {
			System.out.println(" " + j);
			j++;
		}while(j<=10);
	}

}
